mapper.select:
select

from users
where username = #{userName} and password = #{password}

mapper.delete:
delete
from follows
where uid = #{uid}
  and platform = #{platform}
  and owner = #{owner}
  and repo = #{repo}

mapper.update:
update users
set nickname = #{nickName}
where username = #{userName}

update users
set password = #{password}
where username = #{userName}
